import { db } from '../../lib/db'
import admin from 'firebase-admin'

const saveStatus = async (req, res) =>{
    await db.collection('teste')
        .add({
            test: 2,
            coordinates: new admin.firestore.GeoPoint(-43.2222, -60.3233)
        })
    console.log(req)
    res.send({ok: true})
}
export default saveStatus
